import  React, {Component} from 'react';
import axios from 'axios';


// import './css/LandingPage.css';


export default  class Create extends  Component{


    constructor(props) {
        super(props);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.onChangeNIC = this.onChangeNIC.bind(this);
        this.onChangePhone = this.onChangePhone.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangecPassword = this.onChangecPassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            address: '',
            nic:'',
            phone:'',
            email:'',
            password:'',
            cpassword:''
        }
    }
    onChangeName(e){
        this.setState( {
           name: e.target.value
        });
    }
    onChangeAddress(e){
        this.setState( {
            address: e.target.value
        });
    }
    onChangeNIC(e){
        this.setState( {
            nic: e.target.value
        });
    }
    onChangePhone(e){
        this.setState( {
            phone: e.target.value
        });
    }

    onChangeEmail(e){

            this.setState({
                email: e.target.value
            });
    }
    onChangePassword(e){
        this.setState( {
            password: e.target.value
        });
    }
    onChangecPassword(e){
        this.setState( {
            cpassword: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            name : this.state.name,
            address : this.state.address,
            nic : this.state.nic,
            phone : this.state.phone,
            email : this.state.email,
            password : this.state.password
        };
       
        const lastelement = this.state.nic.charAt(this.state.nic.length - 1);
       
        
        if(this.state.password ===  this.state.cpassword){
            if(this.state.password.length >= 8){
                if(this.state.phone.length === 10){
                    if(this.state.nic.length === 10){
                        if(lastelement === 'V' || lastelement === 'v'){
                            axios.post('http://localhost:4000/trainFood/add',obj)
                                .then(res => {
                                    alert("Registration Successfully");
                                    this.setState({
                                        name :'',
                                        address :'',
                                        nic :'',
                                        phone:'',
                                        email:'',
                                        password:'',
                                        cpassword:''
                            
                                    })
                                    console.log(res.data)});
                            this.props.history.push('/signIn');
                        } 
                        else {
                            alert('Invalid NIC Number.. Pleace enter "V" for nic.');
                        }
                    } 
                    else {
                        alert('Invalid NIC Number.. Pleace enter 10 digits for nic.');
                    }
                }
                else{
                    alert('Invalid phone number.. Pleace enter 10 numbers for contact number.');
                }
            } 
            else {
                alert('Pleace enter at least 8 characters for passwords.');
            }
        }else{
            alert('Your password and confirm password are miss match... Pleace enter same passwords');
        }

    }

    render() {
        return(
            <div className='reg-container'>
                <div className='log-top'>
                    {/* <h2>Login</h2> */}
                    <img src = "https://images.pond5.com/artistic-concept-painting-beautiful-train-illustration-209134821_iconl_wide_nowm.jpeg"/>
                </div>
                <br/>
                <div className='reg-bottom'>
                    <div className='reg-bottom-inner'>
                        <br/>
                        <h3>User Login Details</h3>
                        <img src = "https://img.freepik.com/premium-vector/people-profile-graphic_24911-21373.jpg"/>
                        <br/>
                        <div className="container" style={{marginTop:10}}>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>Username :</label>
                                    <input type ="text" required className="form-control" placeholder="raone@gmail.com" value={this.state.email} onChange = {this.onChangeEmail}/>
                                </div>
                                <div className="form-group">
                                    <label>Password :</label>
                                    <input type ="password" required className="form-control" placeholder="********" value={this.state.password} onChange = {this.onChangePassword}/>
                                </div>
                                <div className="form-group" >
                                    <input type = "submit" value = "Sign In" className="btn btn-primary"/>
                                </div>
                            </form>

                            <a href ="/registeration">Create New Account</a>
                           

                            
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
