import  React, {Component} from 'react';
import axios from 'axios'

import './css/cusProfile.css';


export default  class CusAddPayment extends  Component{


    constructor(props) {
        super(props);
        this.state = {customers : []};
        this.state.Email = this.props.match.params.id;

        this.onChangeFirstName = this.onChangeFirstName.bind(this);
        this.onChangeLastName = this.onChangeLastName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangeAmount = this.onChangeAmount.bind(this);

        this.onChangeCardNumber = this.onChangeCardNumber.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeCVV = this.onChangeCVV.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            fname: '',
            lname:'',
            email: '',
            amount:'',
            cardnumber:'',
            date:'',
            cvv:'',
            status:''
        }
    }
    onChangeFirstName(e){
        this.setState( {
           fname: e.target.value
        });
    }
    onChangeLastName(e){
        this.setState( {
           lname: e.target.value
        });
    }
    onChangeEmail(e){
        this.setState( {
           email: e.target.value
        });
    }
    onChangeAmount(e){
        this.setState( {
           amount: e.target.value
        });
    }
    onChangeCardNumber(e){
        this.setState( {
            cardnumber: e.target.value
        });
    }
    onChangeDate(e){
        this.setState( {
            date: e.target.value
        });
    }
    onChangeCVV(e){
        this.setState( {
            cvv: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        this.state.status = 'Pendding';
        const obj = {
            fname : this.state.fname,
            lname : this.state.lname,
            email : this.state.email,
            amount : this.state.amount,
            cardnumber : this.state.cardnumber,
            date : this.state.date,
            cvv : this.state.cvv,
            status : this.state.status
        };
       
        
        if(this.state.amount <= 0 ){
                            axios.post('http://localhost:4000/payment/cusaddpayment',obj)
                                .then(res => {
                                    alert("Registration Successfully");
                                    this.setState({
                                        fname: '',
                                        lname:'',
                                        email: '',
                                        amount:'',
                                        cardnumber:'',
                                        date:'',
                                        cvv:''
                                    })
                                    console.log(res.data)});
                            this.props.history.push('/viewpayment/'+this.state.email);
               
        }else{
            alert('Enter Valide Payment Amount...');
        }

    }

    render() {
        return(
                <div>
                     <div class="sidebar">
                        <br/>
                        <center>
                           <img src = "https://www.pngall.com/wp-content/uploads/5/Profile-Male-PNG.png" width="100"/>
                           <br/>
                           <h6>{this.props.match.params.id}</h6>
                        </center>
                        
                        <a href= {"/profile/" +this.props.match.params.id}>Profile</a>
                        <a href={"/myorder/" +this.props.match.params.id}>Reserved Rooms</a>
                        <a href="/about">Booked Vehicle</a>
                        <a href="/contact">Event & Outdor Activity</a>
                        <a href="/contact">Destination</a>
                        <a href="/contact">Booked Tour Guides</a>
                        <a href="/contact">Insurance</a>
                        <a href={"/addpayment/"+this.props.match.params.id}>Payment</a>
                        <a href="/">SignOut</a>
                    </div>

                    <div class="content">
                        <h2 className= 'tittle'>Fast Food</h2>

                        <br/>
                        <h3 align="center">Customer Payment Form</h3>
                       
                        <br/> <br/> <br/>
                        <div className="container" style={{marginLeft:1000}}>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>Name :</label>
                                    <input type ="text" required className="form-control" placeholder="enter name" value={this.state.name} onChange = {this.onChangeName}/>
                                </div>
                                <div className="form-group">
                                    <label>Email :</label>
                                    <input type ="email" required className="form-control" placeholder="enter email" value={this.state.email} onChange = {this.onChangeEmail}/>
                                </div>
                                <div className="form-group">
                                    <label>Date of Birth :</label>
                                    <input type ="date" required className="form-control" value={this.state.dob} onChange = {this.onChangeDoB}/>
                                </div>
                                <div className="form-group">
                                    <label>Gender :</label>
                                    <select className="form-control" onChange = {this.onChangeGender} value={this.state.gender}>
                                        <option value="Male">Male</option>
                                        <option value="FeMale">FeMale</option>
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>City :</label>
                                    <input type ="text" required className="form-control" placeholder="enter city" value={this.state.city} onChange = {this.onChangeCity}/>
                                </div>
                                <div className="form-group">
                                    <label>Contact Number :</label>
                                    <input type ="number" required className="form-control" placeholder="enter phone number" value={this.state.phone} onChange = {this.onChangePhone}/>
                                </div>
                                <div className="form-group">
                                    <label>Password :</label>
                                    <input type ="password" required className="form-control" placeholder="enter password" value={this.state.password} onChange = {this.onChangePassword}/>
                                </div>
                                <div className="form-group">
                                    <label>Confirm Password :</label>
                                    <input type ="password" required className="form-control" placeholder="enter confirm password" value={this.state.cpassword} onChange = {this.onChangeCPassword}/>
                                </div>
                                <div className="form-group" >
                                    <input type = "submit" value = "Register" className="btn btn-primary"/>
                                </div>
                            </form>

                            <a href ="/">Already Have an Account</a>
                            
                        </div>
                        
                    </div>
                </div>
        );
    }
}
